#!/bin/sh
# Code generated by go run ./cli-test/internal/gen-testcases/... DO NOT EDIT!

run_test() {
    message=$1
    want=$2
    entrypoint=$3
    shift 3
    got=$( ${entrypoint} $@ )

    if [ "${got}" = "${want}" ]; then
        echo "OK: ${message}"
    else
        echo "NG: ${message}"
        echo "  execution: ${entrypoint} $@"
        echo "  want:      '${want}'"
        echo "  got:       '${got}'"
    fi
}


run_test 'root command should handle positional arguments with zero variadic arguments' \
    '_123_true_abc_' \
    go run ./*.go 123 true abc

run_test 'root command should handle positional arguments with multiple variadic arguments' \
    '_123_true_abc_x,y,z,w' \
    go run ./*.go 123 true abc x y z w

